Tail Attack.flags.wfrp4e.script:
let actorSize = game.wfrp4e.config.actorSizeNums[args.actor.details.size.value]
let attackerSize = game.wfrp4e.config.actorSizeNums[args.attacker.details.size.value]

if (attackerSize > actorSize)
{
   args.actor.addCondition("prone")
   ChatMessage.create({content: `<b>Coletazo</b>: ${args.actor.prototypeToken.name} está ahora @Condition[Tumbado]`, speaker : {alias: args.attacker.prototypeToken.name}})
}

Stealthy.flags.wfrp4e.script:
if (args.type == "skill" &&  args.item.name.includes("Movimiento silencioso"))
{
   let sl = this.actor.characteristics.ag.bonus
   args.prefillModifiers.slBonus += sl;
}

