injury = injury.toObject()
 let teeth = (await new Roll("1d10").roll()).total
 ChatMessage.create({content : `<b>${this.actor.prototypeToken.name}</b> pierde ${teeth} dientes`, speaker : {alias: this.effect.label}})
injury.location.value = `${teeth} ${injury.location.value}`
 args.actor.createEmbeddedDocuments("Item", [injury])
})

