Regenerate.flags.wfrp4e.script:
let chatData = {content: "", whisper: ChatMessage.getWhisperRecipients("GM")}

let wounds = duplicate(this.actor.status.wounds)
let regen = (await new Roll("1d10").roll()).total

if (wounds.value >= wounds.max)
   return

if (wounds.value > 0)
{
   wounds.value += regen
   if (wounds.value > wounds.max) 
        wounds.value = wounds.max
   chatData.content += `<b>${this.actor.name}</b> recupera ${regen} Heridas.`

    if (regen == 10)
       chatData.content += `<br>Adicionalmente, regenera una Herida Crítica.`
}
else if (regen >= 8)
{
    chatData.content += `<b>${this.actor.name}</b> sacó un ${regen} y recupera 1 Herida.`
    wounds.value += 1
    if (regen == 10)
       chatData.content += `<br>Adicionalmente, regenera una Herida Crítica.`
}
else
{
    chatData.content += `<b>${this.actor.name}</b> Tirada de Regenerar de ${regen} - Sin efecto.`
}

this.actor.update({"system.status.wounds" : wounds})
ChatMessage.create(chatData)

