Faxtoryll.flags.wfrp4e.script:
let bleeding = this.actor.hasCondition("bleeding")
if (bleeding)
{
   ui.notifications.notify(`Eliminados ${bleeding.flags.wfrp4e.value} Estados de Sangrando`)
   this.actor.removeCondition("bleeding", bleeding.flags.wfrp4e.value)
}

Healing Draught.flags.wfrp4e.script:
let wounds = this.actor.status.wounds
if (wounds.value == 0)
  return ui.notifications.error("Sin efecto con 0 Heridas")

ui.notifications.notify(`Curadas ${this.actor.characteristics.t.bonus} Heridas`)
this.actor.modifyWounds(this.actor.characteristics.t.bonus)

Salwort.flags.wfrp4e.script:
let stunned= this.actor.hasCondition("stunned")
if (stunned)
{
   ui.notifications.notify(`Eliminado 1 Estado de Aturdido`)
   this.actor.removeCondition("stunned", 1)
}

Vitality Draught.flags.wfrp4e.script:
let fatigued= this.actor.hasCondition("fatigued")
if (fatigued)
{
   ui.notifications.notify(`Eliminados ${fatigued.conditionValue} Estados de Fatiga`)
   this.actor.removeCondition("fatigued", fatigued.conditionValue)
}
