scriptData.script:
let test = await this.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {skipTargets: true, appendTitle :  ` - ${this.effect.name}`, fields : {difficulty: "veasy"}})
await test.roll();
if (test.failed)
{
      this.script.notification("Desarrollas una <strong>Herida supurante</strong>")
      let item = await fromUuid("Compendium.wfrp4e-core.items.kKccDTGzWzSXCBOb")
      this.actor.createEmbeddedDocuments("Item", [item.toObject()])
}
else
{
    this.script.notification("Evitada una <strong>Herida supurante</strong>")
}

let location = this.item.system.location.key;

if (location)
{
    let dropped = this.item.system.weaponsAtLocation;

    if (dropped.length)
    {
        this.script.notification(`Soltado ${dropped.map(i => i.name).join(", ")}!`)
        for(let weapon of dropped)
        {
            await weapon.system.toggleEquip();
        }
    }
}

let roll = await new Roll("max(1, 1d10 - @system.characteristics.t.bonus)", this.actor).roll()

roll.toMessage(this.script.getChatData({flavor : `${this.effect.name} (Duration)`}));

this.effect.updateSource({"duration.rounds" : roll.total})

let location = this.item.system.location.key;

if (location)
{
    let dropped = this.item.system.weaponsAtLocation;

    if (dropped.length)
    {
        this.script.notification(`Soltado ${dropped.map(i => i.name).join(", ")}!`)
        for(let weapon of dropped)
        {
            await weapon.system.toggleEquip();
        }
    }

}

if (this.actor.hasCondition("broken"))
{
    this.actor.removeCondition("broken")
    this.script.notification(`No puede tener Quebrantado`);
}

let item = await fromUuid("Compendium.wfrp4e-core.items.Item.8pVzgPkgWpTJvfhG");
let data = item.toObject();

data.name += " (Enemigo)";

this.actor.createEmbeddedDocuments("Item", [data], {fromEffect: this.effect.id})

let resistance
if (this.item.name.includes("(") && !this.item.name.toLowerCase().includes("(cualquiera)"))
{
	resistance = this.item.parenthesesText
}
else 
{
    resistance = await Dialog.wait({
        title : "Escribe grupo",
                content : "<input type='text'>",
        buttons : {
                    confirm : {
                label : game.i18n.localize("Confirm"),
                        callback : (dlg) => {
                    return dlg.find("input")[0].value
        }
                    }
                },
	default : "confirm",
                close : () => {
            return ""
                }
            })

    if (resistance)
    {
        this.item.updateSource({name :  `${this.item.name.split("(")[0].trim()} (${resistance})`})
        this.effect.updateSource({name : this.effect.name + ` (${resistance})`})
    }
}    
this.item.updateSource({"system.tests.value" : this.item.system.tests.value.replace("la Amenaza asociada", resistance)})

if (resistance && !this.effect.name.includes("("))
{
    this.effect.updateSource({name : this.effect.name += ` (${resistance})`})
}

let talents = (await Promise.all([game.wfrp4e.tables.rollTable("talents"), game.wfrp4e.tables.rollTable("talents"), game.wfrp4e.tables.rollTable("talents")])).map(i => i.text);
let items = [];

for (let talent of talents)
{
    let talentItem = await game.wfrp4e.utility.findTalent(talent)
    if (talentItem)
    {
        items.push(talentItem.toObject());
    }
    else 
    {
        ui.notifications.warn(`No se pudo encontrar ${talent}`, {permanent : true})
    }
}

this.script.notification(`Añadidos ${talents.join(", ")}`);
await this.actor.createEmbeddedDocuments("Item", items, {fromEffect: this.effect.id});

const templateMap = {
    'P2e7Yx98bK3u110a' : "",
    'iuMp3KLaMT2WCmie' : "4s01nHFKVKTEZd3B", 
    'RBuYcT5tppwcmnC5' : "h3yuJDWnixliXeBG", 
    'vcGpNwNbhvfzVveQ' : "7QrsbofccMOE1YsF", 
    'jmhKZy0w9TzkEK9c' : "scVTPVyDDbli4WZL", 
    '9Byj6k7SmdTYis2V' : "4Cd7Dknee2WjReOo", 
    'laJwc2l9tzJPgaaJ' : "2hJ2a5YjbwZWWYrd",
}
let template = (await game.wfrp4e.tables.rollTable("hireling-templates")).object;
let physicalQuirk = (await game.wfrp4e.tables.rollTable("physical-quirks")).text;
let workEthic = (await game.wfrp4e.tables.rollTable("work-ethic")).text;
let personalityQuirk = (await game.wfrp4e.tables.rollTable("personality-quirks")).text;


let templateItem = await warhammer.utility.findItemId(templateMap[template._id]);

let bio = 
`
<p><strong>Plantilla</strong>: ${template.text}</p>
<p><strong>Rareza física</strong>: ${physicalQuirk}</p>
<p><strong>Ética laboral</strong>: ${workEthic}</p>
<p><strong>Rareza de la personalidad</strong>: ${personalityQuirk}</p>
`

await this.actor.update({"system.details.gmnotes.value" : bio})

if (templateItem)
{
    this.actor.createEmbeddedDocuments("Item", [templateItem.toObject()])
}
