let blows = this.item.getFlag("wfrp4e", "blows") || 0
let difficulties = Object.keys(game.wfrp4e.config.difficultyLabels)

blows = Math.clamped(blows, 0, difficulties.length - 1)
if (this.item.system.protects[args.opposedTest.result.hitloc.value])
{
    let difficulty = difficulties[blows];
    this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {fields: {difficulty}, skipTargets: true, appendTitle :  ` - ${this.effect.name}`}).then(async test => {
        await test.roll();
        if (test.failed)
        {
            this.script.message(`${this.actor.name} debe retirarse del combate y huir hasta superar un Chequeo <strong>Desafiante (+0)</strong> de <strong>Voluntad</strong>.`);
            this.item.setFlag("wfrp4e", "failedCool", true);
        }
    })
}

if (args.opposedTest.result.differenceSL >= 0 && args.opposedTest.result.differenceSL <= 2 && args.opposedTest.result.winner == "attacker")
{ 
   this.actor.update({"system.status.fortune.value" : Math.max(0, (this.actor.system.status.fortune?.value - 1) || 0)})
    this.script.message(`Punto de Fortuna robado de <strong>${this.actor.name}</strong>`, {blind : true, whisper : ChatMessage.getWhisperRecipients("GM")})

}

// This script needs to be separate because equipTransfer is off on the other effect, and thus won't execute when added to an actor

let mainEffect = this.item.effects.contents[0];
if (mainEffect.name.includes("<Saber>"))
{
    let choice = await ItemDialog.create(ItemDialog.objectToArray(game.wfrp4e.config.magicLores, this.item.img).sort(WFRP_Utility.nameSorter), 1, "Escoge Saber");
    if (choice.length)
    {
        mainEffect.update({name : mainEffect.name.replace("<Saber>", choice[0].name)})
        this.item.update({name : this.item.name += ` (${choice[0].name})`})
    }
}

this.effect.delete();

if (args.opposedTest.result.differenceSL >= 0 && args.opposedTest.result.differenceSL <= 3 && args.opposedTest.result.winner == "attacker")
{ 
    this.script.message(`Todo el mundo a 20 pies pierde 1 Herida y debe realizar un Chequeo <strong>Complicado (-10)</strong> de <strong>Aguante</strong> o sufrir un Estado de @Condition[Deafened]`, {blind : true,  whisper : ChatMessage.getWhisperRecipients("GM")})
}

if (args.test.result.critical && args.test.result.roll % 10 == 0) 
{
    game.wfrp4e.tables.findTable("knuckleduster-diseases").roll().then(roll => {
      let results = roll.results[0]

      this.script.message(`<strong>${this.actor.name}</strong> contrae @UUID[Compendium.${results.documentCollection}.${results.documentId}]{${results.description}}`, {blind : true, whisper: ChatMessage.getWhisperRecipients("GM") })
    })

  }

if (args.opposedTest.result.differenceSL >= 0 && args.opposedTest.result.differenceSL <= 2 && args.opposedTest.result.winner == "attacker")
{ 
    this.script.message(`Emite una nube de pólvora maloliente. Activa el Efecto Activo <strong>Penaliazador a Empatía</strong> en @UUID[${this.actor.uuid}].`, {blind : true, whisper : ChatMessage.getWhisperRecipients("GM")})
}

if (args.opposedTest.result.differenceSL >= 0 && args.opposedTest.result.differenceSL <= 2 && args.opposedTest.result.winner == "attacker")
{ 
    this.script.message(`Se queda incrustada en la armadura o piel del oponente. Ver @UUID[${this.item.uuid}]{${this.item.name}}.`, speaker : {alias : this.item.name}, {blind: true, whisper : ChatMessage.getWhisperRecipients("GM")})
}

if (args.test.item?.type == "skill" && args.test.item.name.includes(game.i18n.localize("NAME.Stealth")))
{ 
     args.test.result.description = "Astounding Failure";
