scriptData.script:
let item = await fromUuid("Compendium.wfrp4e-core.items.vMYEkrWj0ip6ZOdv");
let data = item.toObject();
data.name += " (Veneno, Enfermedad y Caos)"
this.actor.createEmbeddedDocuments("Item", Array(this.effect.sourceTest.result.overcast.usage.other.current).fill(data), {fromEffect: this.effect.id})

let arm = await fromUuid("Compendium.wfrp4e-core.items.MnMZv7ZXoRqoH9dS");
let leg = await fromUuid("Compendium.wfrp4e-core.items.k00PimCWkff11IA0");

let choice = await ItemDialog.create([arm, leg], 1, "Elige extremidad")

this.actor.createEmbeddedDocuments("Item", choice, {fromEffect: this.effect.id})

let scythe = (await fromUuid("Compendium.wfrp4e-core.items.CXg7XOFJwu4LZ9LM")).toObject();
    scythe.name = "Guadaña de Shyish";
    scythe.system.damage.value = "WPB + 3"
    scythe.system.equipped = true;
    scythe.img = this.effect.icon;
    scythe.system.qualities.value.push({name : "magical"})
    this.actor.createEmbeddedDocuments("Item", [scythe], {fromEffect : this.effect.id})

let woundsGained = Math.min(args.actor.status.wounds.value, args.totalWoundLoss)
woundsGained = Math.ceil(woundsGained / 2)

args.attacker.update({"system.status.wounds.value" : args.attacker.system.status.wounds.value + woundsGained})

args.actor.addCondition("fatigued")
args.attacker.hasCondition("fatigued")?.delete();

this.script.message(`<b>${args.attacker.prototypeToken.name}</b> gana ${woundsGained} Heridas`);

if (this.actor.type == "character")  
  this.actor.corruptionDialog("menor")

let test = await this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {skipTargets: true, appendTitle :  " - " + this.effect.name})
await test.roll();
if(test.failed)
{
    await this.actor.addCondition("unconscious");
    let secondTest = await this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {fields : {difficulty : "easy"}, skipTargets: true, appendTitle :  " - Desesperación"})
    await secondTest.roll();
    if(secondTest.failed)
    {
         await this.actor.addCondition("fatigued");
    }
}

let test = await this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {fields : {difficulty: "average"}, skipTargets: true, appendTitle :  " - " + this.effect.name})
await test.roll();
if(test.failed)
{
    await this.actor.addCondition("stunned");
    let secondTest = await this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {fields : {difficulty: "easy"}, skipTargets: true, appendTitle :  " - Desesperación"})
    await secondTest.roll();
    if(secondTest.failed)
    {
        this.effect.updateSource({name : "El sabor de la muerte"})
         await this.actor.addCondition("fatigued");
    }
    else 
    {
        return false;
    }
}

this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {appendTitle: ` - ${this.effect.name}`, fields: { difficulty: "average" } }).then(async test => {
    await test.roll()
    if (test.failed) {
        this.actor.modifyWounds(-1);
        this.script.message("Recibe 1 de Daño")
    }
})

let strLoss = Math.ceil(CONFIG.Dice.randomUniform() * 10)
let tghLoss = Math.ceil(CONFIG.Dice.randomUniform() * 10)

if (!this.actor.has("Undead") && !this.actor.has("Daemonic")) 
{
    this.actor.setupSkill(game.i18n.localize("NAME.Cool"), { appendTitle: " - " + this.effect.name, fields: { difficulty: "average" }, context: { failure: `Pierde ${strLoss} de Fuerza y ${tghLoss} de Resistencia` } }).then(async test => {
        await test.roll();
        if (test.failed) {
            this.actor.update({ "system.characteristics.s.initial": this.actor.system.characteristics.s.initial - strLoss, "system.characteristics.t.initial": this.actor.system.characteristics.t.initial - tghLoss })
        }
    })

}
else {
    this.script.notification(`<strong>${this.actor.name}</strong> es inmune a ${this.effect.name}`)
}

let items = this.effect.itemTargets;
let msg = "";
for(let item of items)
{
    if (item.system.properties.qualities.durable)
    {
        await item.update({"system.qualities.value" : []});
        msg += `<p>${item.name} pierde todas sus Cualidades</p>`
    }
    else 
    {
        msg += `¡<p>${item.name} se convierte en polvo!</p>` 
        await item.update({name : item.name + " (Polvo)"})
    }
}
if(msg)
{
    this.script.message(msg);
}

let caster = this.effect.sourceActor

if (caster)
{
    let bonus = caster.system.characteristics.wp.bonus
    this.actor.modifyWounds(bonus)

    this.script.message(`<strong>${this.actor.prototypeToken.name}</strong> recupera ${bonus} Heridas`)
}

let healed = parseInt(this.effect.sourceTest.result.SL)
this.actor.modifyWounds(healed)
this.script.message(`Sanadas ${healed} Heridas`)

let blunt = await foundry.applications.api.DialogV2.confirm({window: {title : this.effect.name}, content :`<p>¿Aplicar reducción del daño contundente? (-3)</p>`})

if (blunt)
{
    args.modifiers.other.push({label : this.effect.name, details : "Reducción del daño contundente", value : -3})
}

let item = await fromUuid("Compendium.wfrp4e-core.items.1dUizIgLBgn4jICC");
let data = item.toObject();
data.name += " (Boscoso)";
this.actor.createEmbeddedDocuments("Item", Array(this.effect.sourceTest.result.overcast.usage.other.current).fill(data), {fromEffect : this.effect.id})

if (this.actor.hasCondition("ablaze"))
{
    this.script.notification("Inmune a Ardiendo")
    await this.actor.hasCondition("ablaze")?.delete()
}

if (args.test.result.castOutcome == "success")
{
    CorruptionMessageModel.createCorruptionMessage("moderada", this.script.getChatData())
}

