    amberTalons.name = "Garras de ámbar";
    amberTalons.img = this.effect.img;
    amberTalons.system.damage.value = "SB + WPB"
    amberTalons.system.equipped = true;
    amberTalons.system.qualities.value.push({name : "magical"})
    amberTalons.effects.push({
        label : "Garras de ámbar",
        transfer: false,
        icon : "modules/wfrp4e-core/icons/spells/amber-talons.png" ,
        system: {
          transferData : {
                  documentType : "Item"
              },
              scriptData : [{
                  trigger : "applyDamage",
                  script : "if (args.totalWoundLoss >= 1)\n{ \n    args.actor.addCondition(\"bleeding\")\n}"
              }]
          }
      })
    this.actor.createEmbeddedDocuments("Item", [amberTalons], {fromEffect: this.effect.id})

let darkvision = await fromUuid("Compendium.wfrp4e-core.items.Item.JQa5DLnTs2SEzRrc")
let fear = await fromUuid("Compendium.wfrp4e-core.items.Item.pTorrE0l3VybAbtn")
let acutesense = await fromUuid("Compendium.wfrp4e-core.items.Item.9h82z72XGo9tfgQS")
fear = fear.toObject();
fear.system.specification.value = 1;
acutesense = acutesense.toObject();
acutesense.name += " (Olfato)";
this.actor.createEmbeddedDocuments("Item", [darkvision, fear, acutesense], {fromEffect : this.effect.id})

scriptData.hideScript:
if (args.context.terror || args.extendedTest?.getFlag("wfrp4e", "fear"))
{
	args.abort = true;
	this.script.notification("No necesita hacer Chequeos de Miedo o Terror");
}

scriptData.script:
let test = await args.actor.setupSkill(game.i18n.localize("NAME.Cool"), {skipTargets: true, appendTitle :  " - " + this.effect.name})
await test.roll();

if (test.succeeded)
{
    if (args.totalWoundLoss <= parseInt(test.result.SL))
    {
        args.abort = `<strong>${this.effect.name}</strong>: Ataque desviado o reflejado`
    }
    args.modifiers.other.push({label : this.effect.name, value : -1 * parseInt(test.result.SL)})
}

let test = await this.actor.setupCharacteristic("wp", {skipTargets: true, appendTitle :  ` - ${this.effect.name}`, context : {failure : `<strong>Desconcertado</strong>: Determina el comportamiento con la tabla @Table[bewilder].`}})
await test.roll();
return test.failed

if (this.actor.uuid != this.effect.sourceActor.uuid)
{
    this.actor.setupSkill(game.i18n.localize("NAME.Athletics"), {skipTargets: true, appendTitle :  ` - ${this.effect.name}`, fields : {difficulty: "hard"}, context : {failure : `<strong>${this.effect.name}</strong>: no puede mover o realizar acciones.`}}).then(test => {
       test.roll();
    })
}

let actor = Array.from(game.user.targets)[0]?.actor;

if (actor)
{
    actor.applyEffect({effectUuids : this.effect.sourceItem.effects.contents[0].uuid})
}
else
{
    this.script.notification("¡Sin objetivo!", "error")
}

if ((args.opposedTest.attackerTest.item && args.opposedTest.attackerTest.item.isMelee) || (args.opposedTest.attackerTest.item && !args.opposedTest.attackerTest.item.name.includes("A distancia")))
{
    let choice = await foundry.applications.api.DialogV2.confirm({window : {title : this.effect.name}, content : `<p>¿Aplicar daño con <strong>${this.effect.name}</strong> al atacante?`})
    if (choice)
    {
        this.script.message(await args.attacker.applyBasicDamage(this.actor.system.characteristics.wp.bonus, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_AP, suppressMsg : true}));
    }
}

if (this.actor.hasCondition("surprised"))
{
    this.actor.removeCondition("surprised")
    this.script.message(`No puede ser Sorprendido`);
}

let useWard = await foundry.applications.api.DialogV2.confirm({
	content : `<p>¿Usar Custodiado provisto por <strong>${this.effect.name}</strong>?`,
	window : {
		title : this.effect.name,	}
})

if (useWard)
	args.ward = 9;

let ablaze = this.actor.hasCondition("ablaze");
if (ablaze)
{
	ablaze?.delete();
	this.script.notification("Ignorar Ardiendo");
}

let roll = (await new Roll("1d10").roll())
roll.toMessage({flavor: "Heridas curadas", speaker : {alias : this.actor.name}})

this.actor.modifyWounds(roll.total)

this.actor.hasCondition("bleeding")?.delete();

if (args.test.result.fumble && !this.actor.itemTypes.talent.find(i => i.name == "Magia arcana (Fuego)"))
{
    this.actor.addCondition("ablaze");
}

let aoeDamage = this.effect.sourceTest.result.damage - 5 // Easily handle magic missile damage by just subtracting 5 from the item's (which has +10 base)

this.script.message(await this.actor.applyBasicDamage(aoeDamage, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_AP, suppressMsg : true}))

